```{=html}
<style>
:root{
  --ink:#0f172a; --muted:#64748b; --line:#e2e8f0; --card:#ffffff; --brand:#4f46e5;
}
@media (prefers-color-scheme: dark){
  :root{ --ink:#e5e7eb; --muted:#94a3b8; --line:#1f2937; --card:#0b1220; }
}
.page{max-width:900px;margin:0 auto;padding:16px;}
.navbar{
  display:flex;gap:10px;align-items:center;justify-content:space-between;
  position:sticky;top:0;z-index:5;background:color-mix(in srgb,var(--card) 92%, transparent);
  border-bottom:1px solid var(--line);padding:10px 0;backdrop-filter:blur(6px);
}
.btn{
  border:1px solid var(--line);background:var(--card);color:var(--ink);
  padding:10px 14px;border-radius:10px;cursor:pointer;
}
.btn:hover{border-color:var(--brand)}
.counter{color:var(--muted);font-weight:600}
.stage{margin:16px 0;}
.qcard{
  border:1px solid var(--line);border-radius:14px;background:var(--card);
  box-shadow:0 10px 30px rgba(2,6,23,.08); padding:16px; display:none;
}
.qcard.active{display:block;}
.qhead{display:flex;justify-content:space-between;align-items:center;margin-bottom:10px;}
.qtitle{font-weight:800;color:var(--ink)}
.qactions{display:flex;gap:8px}
.small{font-size:13px;color:var(--muted)}
.footer{
  display:flex;justify-content:center;gap:10px;margin:14px 0 4px;
}
.hidden{display:none !important;}
</style>
```

<script>
  const SLIDE_ID = "module1_p1mcq";
</script>

{{< include ../login-start.html >}}

<div class="page">

  <!-- NAV BAR SUPER SIMPLES -->
  <div class="navbar">
    <button class="btn" id="prevBtn" aria-label="Previous question">◀</button>
    <div class="counter"><span id="pos">1</span> / <span id="total">—</span></div>
    <button class="btn" id="nextBtn" aria-label="Next question">▶</button>
  </div>

  <!-- PALCO ONDE OS CARDS APARECEM -->
  <div class="stage" id="qstage"></div>

  <!-- RODAPÉ (SEGUNDA NAVEGAÇÃO) -->
  <div class="footer">
    <button class="btn" id="prevBtn2">◀ Prev</button>
    <button class="btn" id="nextBtn2">Next ▶</button>
  </div>

  <p class="small">Tip: use ← and → to navigate.</p>

  <!-- ===== COLE AQUI SEUS 100 INCLUDES ===== -->
  <div id="all-questions" class="hidden">
    {{< include "mcq/module1_mcq_q1.qmd" >}}
    {{< include "mcq/module1_mcq_q2.qmd" >}}
    {{< include "mcq/module1_mcq_q3.qmd" >}}
    {{< include "mcq/module1_mcq_q4.qmd" >}}
    {{< include "mcq/module1_mcq_q5.qmd" >}}
    {{< include "mcq/module1_mcq_q6.qmd" >}}
    {{< include "mcq/module1_mcq_q7.qmd" >}}
    {{< include "mcq/module1_mcq_q8.qmd" >}}
    {{< include "mcq/module1_mcq_q9.qmd" >}}
    {{< include "mcq/module1_mcq_q10.qmd" >}}
    <!-- Continue até Q100 -->
  </div>
</div>

<div style="text-align:right">
  {{< include "../quiz-utils.html" >}}
</div>

{{< include ../login-end.html >}}

<script>
(function(){
  const source = document.getElementById('all-questions');
  const forms  = source ? [...source.querySelectorAll('form')] : [];
  const stage  = document.getElementById('qstage');

  const cards = forms.map((form, i) => {
    const card = document.createElement('div');
    card.className = 'qcard';
    const head = document.createElement('div'); head.className='qhead';
    const title = document.createElement('div'); title.className='qtitle'; title.textContent = `Question ${i+1}`;
    head.appendChild(title);
    card.appendChild(head);
    card.appendChild(form);
    stage.appendChild(card);
    return card;
  });

  const totalSpan = document.getElementById('total');
  const posSpan   = document.getElementById('pos');
  totalSpan.textContent = String(cards.length || 0);

  let idx = 0;
  function show(i){
    if(!cards.length) return;
    idx = Math.max(0, Math.min(cards.length - 1, i));
    cards.forEach((c, j)=> c.classList.toggle('active', j===idx));
    posSpan.textContent = String(idx+1);
    cards[idx].scrollIntoView({behavior:'smooth', block:'start'});
  }

  function next(){ show(idx + 1); }
  function prev(){ show(idx - 1); }

  const prevBtn  = document.getElementById('prevBtn');
  const nextBtn  = document.getElementById('nextBtn');
  const prevBtn2 = document.getElementById('prevBtn2');
  const nextBtn2 = document.getElementById('nextBtn2');
  [prevBtn, prevBtn2].forEach(b=> b && b.addEventListener('click', prev));
  [nextBtn, nextBtn2].forEach(b=> b && b.addEventListener('click', next));

  window.addEventListener('keydown', (e)=>{
    if(e.key === 'ArrowLeft')  { e.preventDefault(); prev(); }
    if(e.key === 'ArrowRight') { e.preventDefault(); next(); }
  });

  show(0);
})();
</script>

